mapper.update:
update `discuss_post`
set user_id = #{userId}, title = #{title}, content = #{content}, like_count = #{likeCount}, forward_count = #{forwardCount}, comment_count=#{comment_count}, type = #{type}, status = #{status}
where discuss_post_id = #{discussPostId} and is_deleted = 0

mapper.select:
select id, discuss_post_id, user_id, school_code, title, content, like_count, forward_count, comment_count, type, status, create_time, update_time
from `discuss_post`
where update_time <= (select update_time from `discuss_post` where discuss_post_id = #{postId} and is_deleted = 0)
    and is_deleted = 0 and discuss_post_id != #{postId}
order by update_time DESC, id DESC
limit #{limit}
