mapper.select:
select id, sfile_id, sfile_name, sfile_url, sfile_size, download_count, sfile_type, create_by, update_by, create_time, update_time
from sfile
where id > (select id from `sfile` where sfile_id = #{fileId} and is_deleted = 0)
  and is_deleted = 0
order by #{sortField}
limit #{limit}

select id, sfile_id, sfile_name, sfile_url, sfile_size, download_count, sfile_type, create_by, update_by, create_time, update_time
from sfile
where is_deleted = 0
order by #{sortField}
limit #{limit}
